import javax.ws.rs.Produces;
import javax.ws.rs.core.MediaType;
import javax.ws.rs.core.Response;

public class MyProvider {

    @Produces(MediaType.APPLICATION_JSON)
    public Response getResponse() {
        // Implementation goes here
    }

    public void setCustomProduces(MediaType mediaType) {
        Produces producesAnnotation = MyProvider.class.getAnnotation(Produces.class);
        if (producesAnnotation != null) {
            MediaType[] mediaTypes = producesAnnotation.value();
            MediaType[] customMediaTypes = new MediaType[mediaTypes.length + 1];
            System.arraycopy(mediaTypes, 0, customMediaTypes, 0, mediaTypes.length);
            customMediaTypes[mediaTypes.length] = mediaType;
            Produces customProducesAnnotation = new Produces() {
                @Override
                public Class<? extends Annotation> annotationType() {
                    return Produces.class;
                }

                @Override
                public MediaType[] value() {
                    return customMediaTypes;
                }
            };
            ((AnnotatedElement) MyProvider.class).getDeclaredAnnotations()[0] = customProducesAnnotation;
        } else {
            Produces customProducesAnnotation = new Produces() {
                @Override
                public Class<? extends Annotation> annotationType() {
                    return Produces.class;
                }

                @Override
                public MediaType[] value() {
                    return new MediaType[]{mediaType};
                }
            };
            ((AnnotatedElement) MyProvider.class).getDeclaredAnnotations()[0] = customProducesAnnotation;
        }
    }
}

