        List<ByteBuffer> clonedList = new ArrayList<>();

        for (ByteBuffer buffer : originalList) {
            // Create a new ByteBuffer with the same capacity as the original buffer
            ByteBuffer clonedBuffer = ByteBuffer.allocate(buffer.capacity());
            // Set the cloned buffer's position, limit, and mark to match the original buffer
            clonedBuffer.position(buffer.position());
            clonedBuffer.limit(buffer.limit());
            clonedBuffer.mark();
            // Copy the data from the original buffer to the cloned buffer
            clonedBuffer.put(buffer);
            // Reset the cloned buffer's position and mark
            clonedBuffer.flip();

            clonedList.add(clonedBuffer);
        }

