import javax.xml.namespace.NamespaceContext;
import java.util.HashMap;
import java.util.Iterator;
import java.util.Map;

public class NamespaceContextImpl implements NamespaceContext {

    private final Map<String, String> prefixToUriMap;

    public NamespaceContextImpl() {
        prefixToUriMap = new HashMap<>();
    }

    public void setMapping(String uri, String prefix) {
        prefixToUriMap.put(prefix, uri);
    }

    @Override
    public String getNamespaceURI(String prefix) {
        return prefixToUriMap.get(prefix);
    }

    @Override
    public String getPrefix(String namespaceURI) {
        for (Map.Entry<String, String> entry : prefixToUriMap.entrySet()) {
            if (entry.getValue().equals(namespaceURI)) {
                return entry.getKey();
            }
        }
        return null;
    }

    @Override
    public Iterator<String> getPrefixes(String namespaceURI) {
        // We're only mapping a single prefix to a URI in this implementation,
        // so we'll return an iterator with only that prefix if the URI matches.
        if (prefixToUriMap.containsValue(namespaceURI)) {
            return prefixToUriMap.keySet().iterator();
        } else {
            return null;
        }
    }
}

