public Stmt parseWithSubstitutions(String input, Map<String, String> substitutions) throws ParseException {
    // Create a new parser for the input string
    Parser parser = new Parser(new StringReader(input));
    // Parse the input string into a Stmt AST node
    Stmt stmt = parser.stmt();
    // Apply substitutions to the Stmt AST node
    applySubstitutions(stmt, substitutions);
    return stmt;
}

private void applySubstitutions(Stmt stmt, Map<String, String> substitutions) {
    // If the Stmt AST node is a variable, apply the substitution if it exists
    if (stmt instanceof Variable) {
        Variable var = (Variable) stmt;
        String value = substitutions.get(var.getName());
        if (value != null) {
            var.setName(value);
        }
    }
    // If the Stmt AST node is a compound statement, apply substitutions to its children
    else if (stmt instanceof CompoundStmt) {
        CompoundStmt compoundStmt = (CompoundStmt) stmt;
        for (Stmt child : compoundStmt.getChildren()) {
            applySubstitutions(child, substitutions);
        }
    }
    // If the Stmt AST node is an assignment, apply substitutions to its variable and expression
    else if (stmt instanceof AssignStmt) {
        AssignStmt assignStmt = (AssignStmt) stmt;
        applySubstitutions(assignStmt.getVariable(), substitutions);
        applySubstitutions(assignStmt.getExpression(), substitutions);
    }
    // If the Stmt AST node is a print statement, apply substitutions to its expression
    else if (stmt instanceof PrintStmt) {
        PrintStmt printStmt = (PrintStmt) stmt;
        applySubstitutions(printStmt.getExpression(), substitutions);
    }
    // If the Stmt AST node is a conditional statement, apply substitutions to its condition and children
    else if (stmt instanceof ConditionalStmt) {
        ConditionalStmt conditionalStmt = (ConditionalStmt) stmt;
        applySubstitutions(conditionalStmt.getCondition(), substitutions);
        applySubstitutions(conditionalStmt.getTrueBranch(), substitutions);
        applySubstitutions(conditionalStmt.getFalseBranch(), substitutions);
    }
    // If the Stmt AST node is a loop statement, apply substitutions to its condition and children
    else if (stmt instanceof LoopStmt) {
        LoopStmt loopStmt = (LoopStmt) stmt;
        applySubstitutions(loopStmt.getCondition(), substitutions);
        applySubstitutions(loopStmt.getBody(), substitutions);
    }
    // If the Stmt AST node is none of the above, do nothing
}

