public static boolean domainMatch(String hostA, String hostB) {
    if (hostA == null || hostB == null) {
        return false;
    }
    if (hostA.equalsIgnoreCase(hostB)) {
        return true;
    }
    if (hostA.endsWith(hostB)) {
        String domain = hostA.substring(0, hostA.length() - hostB.length());
        if (domain.endsWith(".")) {
            domain = domain.substring(0, domain.length() - 1);
        }
        return isValidHdn(domain);
    }
    return false;
}

private static boolean isValidHdn(String hdn) {
    if (hdn == null || hdn.length() == 0 || hdn.charAt(0) == '.' || hdn.charAt(hdn.length() - 1) == '.') {
        return false;
    }
    String[] parts = hdn.split("\\.");
    for (String part : parts) {
        if (!isValidLabel(part)) {
            return false;
        }
    }
    return true;
}

private static boolean isValidLabel(String label) {
    if (label == null || label.length() == 0 || label.length() > 63) {
        return false;
    }
    for (int i = 0; i < label.length(); i++) {
        char c = label.charAt(i);
        if (!Character.isLetterOrDigit(c) && c != '-') {
            return false;
        }
    }
    if (label.charAt(0) == '-' || label.charAt(label.length() - 1) == '-') {
        return false;
    }
    return true;
}

