public boolean isValidNode(Node node) {
    // A null node is not valid
    if (node == null) {
        return false;
    }

    // A node with null value is not valid
    if (node.value == null) {
        return false;
    }

    // A node with null next reference is valid
    if (node.next == null) {
        return true;
    }

    // If the next reference of the node points to itself, it's not valid
    if (node == node.next) {
        return false;
    }

    // Otherwise, recursively check if the next node is valid
    return isValidNode(node.next);
}

