    private Map<Integer, List<Integer>> graph = new HashMap<>();

    // Method to remove an edge from the graph
    public void removeEdge(int source, int destination) {
        // Check if the source vertex is present in the graph
        if (!graph.containsKey(source)) {
            System.out.println("Source vertex " + source + " not present in the graph");
            return;
        }

        // Get the adjacency list of the source vertex
        List<Integer> edges = graph.get(source);

        // Check if the destination vertex is present in the adjacency list
        if (!edges.contains(destination)) {
            System.out.println("Edge " + source + " -> " + destination + " not present in the graph");
            return;
        }

        // Remove the destination vertex from the adjacency list of the source vertex
        edges.remove(Integer.valueOf(destination));
        graph.put(source, edges);

        System.out.println("Edge " + source + " -> " + destination + " removed from the graph");
    }

    // Other methods for adding vertices and edges to the graph
    // ...
}

