import com.fasterxml.jackson.databind.JsonNode;
import com.fasterxml.jackson.databind.ObjectMapper;

public class GitblitSettingsFetcher {
    private static final String GITBLIT_API_ENDPOINT = "/rpc/serverSettings";

    public static void main(String[] args) {
        String gitblitUrl = "https://example.com/gitblit";
        String username = "your-username";
        String password = "your-password";

        try {
            JsonNode settings = fetchGitblitSettings(gitblitUrl, username, password);
            System.out.println("Gitblit server settings:");
            System.out.println(settings);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static JsonNode fetchGitblitSettings(String gitblitUrl, String username, String password)
            throws IOException {
        HttpClient client = HttpClientBuilder.create().build();
        HttpGet request = new HttpGet(gitblitUrl + GITBLIT_API_ENDPOINT);
        request.addHeader("Accept", "application/json");
        request.addHeader("Authorization", "Basic " + encodeCredentials(username, password));

        HttpResponse response = client.execute(request);
        HttpEntity entity = response.getEntity();
        String json = EntityUtils.toString(entity, "UTF-8");

        ObjectMapper mapper = new ObjectMapper();
        return mapper.readTree(json);
    }

    private static String encodeCredentials(String username, String password) {
        String credentials = username + ":" + password;
        byte[] credentialsBytes = credentials.getBytes();
        return java.util.Base64.getEncoder().encodeToString(credentialsBytes);
    }
}

