import org.junit.Test;

public class BufferedBlockInStreamTest {

    private static final int BLOCK_SIZE = 4;
    private static final byte[] DATA = new byte[] { 1, 2, 3, 4, 5, 6, 7, 8, 9 };
    private BufferedBlockInStream in;

    @Before
    public void setUp() throws Exception {
        ByteArrayInputStream stream = new ByteArrayInputStream(DATA);
        in = new BufferedBlockInStream(stream, BLOCK_SIZE);
    }

    @Test
    public void testSeek() throws IOException {
        // read first block
        byte[] block1 = new byte[BLOCK_SIZE];
        int bytesRead = in.read(block1);
        assertEquals(BLOCK_SIZE, bytesRead);

        // seek to middle of second block
        in.seek(BLOCK_SIZE + 2);

        // read second block from seek position
        byte[] block2 = new byte[BLOCK_SIZE];
        bytesRead = in.read(block2);
        assertEquals(BLOCK_SIZE, bytesRead);
        assertEquals(6, block2[0] & 0xff);
        assertEquals(7, block2[1] & 0xff);
        assertEquals(8, block2[2] & 0xff);
        assertEquals(9, block2[3] & 0xff);
    }
}

