import java.lang.ref.Reference;
import java.lang.ref.ReferenceQueue;
import java.lang.ref.WeakReference;

public class GarbageCollector {
    public static void runFullGCAndWait() throws InterruptedException {
        // Create a weak reference and reference queue
        Object obj = new Object();
        ReferenceQueue<Object> queue = new ReferenceQueue<>();
        WeakReference<Object> ref = new WeakReference<>(obj, queue);

        // Perform a full garbage collection cycle
        System.gc();
        System.runFinalization();
        System.gc();

        // Wait for the reference queue to be cleared
        while (queue.poll() == null) {
            Thread.sleep(10);
        }

        // Wait for the finalize method to be called
        for (int i = 0; i < 10; i++) {
            if (obj == null) {
                break;
            }
            Thread.sleep(10);
        }

        // Ensure that at least one weak reference has been cleared and one finalize method has been run
        if (ref.get() != null || obj != null) {
            throw new AssertionError("Full GC did not clear weak references or run finalize methods");
        }
    }
}

