public class Example {

    public static double[][] inverseMatrix(double[][] matrix) throws Exception {
        int n = matrix.length;

        // Check if matrix is square
        if (n != matrix[0].length) {
            throw new Exception("Matrix is not square");
        }

        double[][] identity = new double[n][n];
        double[][] inverse = new double[n][n];

        // Initialize identity matrix
        for (int i = 0; i < n; i++) {
            identity[i][i] = 1;
        }

        // Perform row operations to obtain upper triangular matrix
        for (int i = 0; i < n; i++) {
            // Pivot element
            double pivot = matrix[i][i];

            // Check if pivot is zero
            if (pivot == 0) {
                throw new Exception("Matrix is singular");
            }

            // Divide row by pivot
            for (int j = 0; j < n; j++) {
                matrix[i][j] /= pivot;
                identity[i][j] /= pivot;
            }

            // Subtract multiple of row from other rows to eliminate entries below pivot
            for (int k = i + 1; k < n; k++) {
                double factor = matrix[k][i];
                for (int j = 0; j < n; j++) {
                    matrix[k][j] -= factor * matrix[i][j];
                    identity[k][j] -= factor * identity[i][j];
                }
            }
        }

        // Perform row operations to obtain identity matrix
        for (int i = n - 1; i > 0; i--) {
            for (int k = i - 1; k >= 0; k--) {
                double factor = matrix[k][i];
                for (int j = 0; j < n; j++) {
                    matrix[k][j] -= factor * matrix[i][j];
                    identity[k][j] -= factor * identity[i][j];
                }
            }
        }

        // Copy inverse to result
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                inverse[i][j] = identity[i][j];
            }
        }

        return inverse;
    }
}

