import java.net.DatagramPacket;
import java.util.UUID;

public class Example {

    public void setServiceID(DatagramPacket packet) {
        // Generate a random UUID to use as the service ID
        UUID serviceID = UUID.randomUUID();

        // Get the data array of the packet
        byte[] data = packet.getData();

        // Create a new byte array to hold the service ID
        byte[] serviceIDBytes = new byte[16];

        // Convert the UUID to a byte array and copy it to the service ID array
        byte[] uuidBytes = getUUIDBytes(serviceID);
        System.arraycopy(uuidBytes, 0, serviceIDBytes, 0, 16);

        // Set the service ID in the data array of the packet
        System.arraycopy(serviceIDBytes, 0, data, 0, 16);

        // Update the data length of the packet
        packet.setData(data, 0, data.length);
    }

    private byte[] getUUIDBytes(UUID uuid) {
        long mostSigBits = uuid.getMostSignificantBits();
        long leastSigBits = uuid.getLeastSignificantBits();

        byte[] uuidBytes = new byte[16];
        for (int i = 0; i < 8; i++) {
            uuidBytes[i] = (byte) (mostSigBits >>> 8 * (7 - i));
        }
        for (int i = 8; i < 16; i++) {
            uuidBytes[i] = (byte) (leastSigBits >>> 8 * (7 - i));
        }

        return uuidBytes;
    }
}

