import com.box.sdk.BoxAPIConnection;
import com.box.sdk.BoxFolder;
import com.box.sdk.BoxItem;
import com.box.sdk.BoxItem.Info;

public class ListFolders {

    public static void main(String[] args) {

        // Authenticate with your Box app token
        BoxAPIConnection api = new BoxAPIConnection("YOUR_APP_TOKEN");

        // Get the root folder
        BoxFolder rootFolder = BoxFolder.getRootFolder(api);

        // Recursively list all folders
        listFolders(rootFolder, "");
    }

    private static void listFolders(BoxFolder folder, String indent) {
        // Get the name of the current folder
        String folderName = folder.getInfo().getName();
        System.out.println(indent + "+--" + folderName);

        // Get all the items in the current folder
        for (BoxItem.Info itemInfo : folder) {
            if (itemInfo instanceof BoxFolder.Info) {
                // If the item is a folder, recursively list its contents
                BoxFolder childFolder = new BoxFolder(itemInfo.getID(), itemInfo.getETag(), itemInfo.getSession());
                listFolders(childFolder, indent + "   ");
            }
        }
    }
}

