if (error != null) {
    // Handle error
}

// Add the video stream to the muxer
int streamIndex = muxer.addNewStream(encoder);

// Set the stream's metadata
muxer.getContainer().getStream(streamIndex).getMetaData().setFromMetaData(metaData);

// Write the header
error = muxer.writeHeader();
if (error != null) {
    // Handle error
}

// Now you can write the encoded video frames to the muxer
