public static List<String> getResources(String extension, String... basePackages) {
    List<String> resources = new ArrayList<>();
    ClassLoader classLoader = Thread.currentThread().getContextClassLoader();

    for (String basePackage : basePackages) {
        String basePath = basePackage.replace(".", "/");
        try {
            Enumeration<URL> urls = classLoader.getResources(basePath);
            while (urls.hasMoreElements()) {
                URL url = urls.nextElement();
                String protocol = url.getProtocol();
                if ("file".equals(protocol)) {
                    File[] files = new File(url.toURI()).listFiles();
                    for (File file : files) {
                        if (file.isFile() && file.getName().endsWith(extension)) {
                            resources.add(file.getPath());
                        }
                    }
                } else if ("jar".equals(protocol)) {
                    String jarPath = url.getPath().substring(5, url.getPath().indexOf("!"));
                    JarFile jarFile = new JarFile(URLDecoder.decode(jarPath, "UTF-8"));
                    Enumeration<JarEntry> entries = jarFile.entries();
                    while (entries.hasMoreElements()) {
                        JarEntry entry = entries.nextElement();
                        if (!entry.isDirectory() && entry.getName().startsWith(basePath) && entry.getName().endsWith(extension)) {
                            resources.add(entry.getName());
                        }
                    }
                    jarFile.close();
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    return resources;
}

