public static double[][] getCofactor(double[][] matrix, int row, int col) {
    int n = matrix.length;
    double[][] cofactor = new double[n - 1][n - 1];

    int i = 0;
    int j = 0;

    for (int r = 0; r < n; r++) {
        if (r == row) {
            continue;
        }
        for (int c = 0; c < n; c++) {
            if (c == col) {
                continue;
            }
            cofactor[i][j++] = matrix[r][c];
            if (j == n - 1) {
                j = 0;
                i++;
            }
        }
    }
    return cofactor;
}

