import java.net.CookieHandler;
import java.net.CookieManager;
import java.net.CookieStore;
import java.net.HttpCookie;
import java.util.Arrays;
import java.util.List;

public class CookieExample {
    public static void addCookiesToStore(CookieStore cookieStore, HttpCookie[] cookies) {
        for (HttpCookie cookie : cookies) {
            if (!cookie.hasExpired()) {
                cookieStore.add(null, cookie);
            } else {
                cookieStore.remove(null, cookie);
            }
        }
    }

    public static void main(String[] args) {
        CookieHandler.setDefault(new CookieManager());
        CookieStore cookieStore = ((CookieManager) CookieHandler.getDefault()).getCookieStore();

        HttpCookie cookie1 = new HttpCookie("name1", "value1");
        cookie1.setMaxAge(60); // expires in 60 seconds
        HttpCookie cookie2 = new HttpCookie("name2", "value2");
        cookie2.setMaxAge(600); // expires in 600 seconds
        HttpCookie cookie3 = new HttpCookie("name3", "value3");
        cookie3.setMaxAge(-1); // persistent cookie

        HttpCookie[] cookies = { cookie1, cookie2, cookie3 };
        addCookiesToStore(cookieStore, cookies);

        List<HttpCookie> storedCookies = cookieStore.getCookies();
        System.out.println("Stored cookies: " + storedCookies);
    }
}

