public class Matrix {
    private double[][] elements;

    /**
     * Perform a shift in a random direction that is of the same magnitude as the elements in the matrix.
     */
    public void shiftInRandomDirection() {
        Random random = new Random();
        double magnitude = getMagnitude();
        double dx = random.nextDouble() * magnitude;
        double dy = random.nextDouble() * magnitude;
        shift(dx, dy);
    }

    /**
     * Get the magnitude of the elements in the matrix.
     * @return the magnitude of the elements in the matrix.
     */
    private double getMagnitude() {
        double magnitude = 0.0;
        for (int i = 0; i < elements.length; i++) {
            for (int j = 0; j < elements[i].length; j++) {
                magnitude += Math.abs(elements[i][j]);
            }
        }
        return magnitude;
    }

    /**
     * Shift the elements in the matrix by the specified amounts in the x and y directions.
     * @param dx the amount to shift the elements in the x direction.
     * @param dy the amount to shift the elements in the y direction.
     */
    private void shift(double dx, double dy) {
        for (int i = 0; i < elements.length; i++) {
            for (int j = 0; j < elements[i].length; j++) {
                elements[i][j] += (i + j) % 2 == 0 ? dx : dy;
            }
        }
    }
}

