public class AfterInterceptor {
    private List<Interceptor> afterInterceptors = new ArrayList<>();

    public AfterInterceptor addAfter(Interceptor interceptor) {
        afterInterceptors.add(interceptor);
        return this;
    }

    public void addToChain(Interceptor.Chain chain) {
        // Get the current interceptors in the chain
        List<Interceptor> currentInterceptors = new ArrayList<>(chain.interceptors());

        // Find the index of the last interceptor from the specified collection
        int lastIndexOfAfter = -1;
        for (Interceptor interceptor : afterInterceptors) {
            int index = currentInterceptors.indexOf(interceptor);
            if (index > lastIndexOfAfter) {
                lastIndexOfAfter = index;
            }
        }

        // Insert the new interceptor after the last one from the specified collection
        chain.interceptors().addAll(lastIndexOfAfter + 1, afterInterceptors);
    }
}

