    byte[] pathBytes = pathElement.getBytes(StandardCharsets.UTF_8);
    int pathLength = pathBytes.length;
    int encodedPathElement = pathLength << 1;

    if ((pathBytes[pathLength - 1] & 0x80) != 0) {
        // Add the most significant bit
        encodedPathElement |= 0x01;
    }

    for (int i = 0; i < pathLength - 1; i++) {
