import org.eclipse.uml2.uml.*;
import org.eclipse.emf.ecore.xmi.*;
import java.io.*;
import org.xml.sax.*;

public class XMIReader {
   public static void main(String[] args) throws Exception {
      String xmiFileName = "path/to/xmi/file";
      String systemId = "systemId/of/xmi/file";

      // Create an XMI resource and load the UML model
      ResourceSet resourceSet = new ResourceSetImpl();
      resourceSet.getResourceFactoryRegistry().getExtensionToFactoryMap().put("xmi", new XMIResourceFactoryImpl());
      Resource resource = resourceSet.createResource(URI.createFileURI(xmiFileName));
      InputStream inputStream = new FileInputStream(xmiFileName);
      resource.load(inputStream, null);

      // Get the root package of the UML model
      Package rootPackage = (Package) resource.getContents().get(0);

      // Get an element in the model and its XMI ID
      NamedElement element = rootPackage.getOwnedType("MyClass");
      String xmiId = getXMIId(element, systemId);

      System.out.println("XMI ID for element " + element.getName() + ": " + xmiId);
   }

   private static String getXMIId(NamedElement element, String systemId) throws SAXException, IOException {
      // Find the XMI resource that contains the element
      XMIResource resource = (XMIResource) element.eResource();
      if (resource == null) {
         throw new RuntimeException("Element does not belong to an XMI resource");
      }

      // Parse the XMI file to find the XMI ID of the element's reference
      SAXParserFactory parserFactory = SAXParserFactory.newInstance();
      parserFactory.setNamespaceAware(true);
      SAXParser parser = parserFactory.newSAXParser();
      XMIHandler handler = new XMIHandler(resource, null, null);
      parser.setProperty("http://xml.org/sax/properties/lexical-handler", handler);
      parser.parse(new InputSource(new StringReader(resource.getXMI())), handler);

      // Return the XMI ID for the element's reference
      String refId = resource.getID(element);
      return handler.getXMIId(refId, systemId);
   }
}

