import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.nio.channels.FileChannel;

public class NotebookBackup {

    // Backup all notebooks
    public void backupAllNotebooks(File sourceDirectory, File backupDirectory) throws IOException {
        // Check if source directory exists and is a directory
        if (!sourceDirectory.exists() || !sourceDirectory.isDirectory()) {
            throw new IllegalArgumentException("Source directory does not exist or is not a directory");
        }

        // Create backup directory if it does not exist
        if (!backupDirectory.exists()) {
            backupDirectory.mkdirs();
        }

        // Iterate over all notebooks in the source directory
        for (File notebook : sourceDirectory.listFiles()) {
            // Check if file is a directory or hidden
            if (notebook.isDirectory() || notebook.isHidden()) {
                continue;
            }

            // Backup the notebook to the backup directory
            backupNotebook(notebook, backupDirectory);
        }
    }

    // Backup a single notebook
    private void backupNotebook(File notebook, File backupDirectory) throws IOException {
        // Create backup file
        File backupFile = new File(backupDirectory, notebook.getName() + ".bak");

        // Create file channels for copying the notebook to the backup file
        try (FileChannel sourceChannel = new FileInputStream(notebook).getChannel();
                FileChannel destinationChannel = new FileOutputStream(backupFile).getChannel()) {
            destinationChannel.transferFrom(sourceChannel, 0, sourceChannel.size());
        }
    }
}

