import java.nio.charset.StandardCharsets;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.util.HashMap;
import java.util.Map;

public class MyGameHashVerifier {
    private Map<String, String> hashToNumber = new HashMap<>();

    public void addHash(String hash, int number) {
        // Compute the hash of the number using SHA-256 algorithm
        String computedHash = computeHash(Integer.toString(number));

        // Add the hash and the corresponding number to the map
        hashToNumber.put(hash, Integer.toString(number));

        // Verify that the computed hash matches the expected hash
        if (!hash.equals(computedHash)) {
            throw new RuntimeException("Hash verification failed");
        }
    }

    public boolean verifyHash(String hash, int number) {
        // Compute the hash of the number using SHA-256 algorithm
        String computedHash = computeHash(Integer.toString(number));

        // Get the number associated with the hash from the map
        String storedNumber = hashToNumber.get(hash);

        // Verify that the computed hash matches the expected hash
        if (storedNumber != null && storedNumber.equals(Integer.toString(number)) && hash.equals(computedHash)) {
            return true;
        } else {
            return false;
        }
    }

    private String computeHash(String message) {
        try {
            MessageDigest digest = MessageDigest.getInstance("SHA-256");
            byte[] hash = digest.digest(message.getBytes(StandardCharsets.UTF_8));
            return bytesToHex(hash);
        } catch (NoSuchAlgorithmException ex) {
            throw new RuntimeException("Failed to compute hash", ex);
        }
    }

    private String bytesToHex(byte[] bytes) {
        StringBuilder result = new StringBuilder();
        for (byte b : bytes) {
            result.append(String.format("%02X", b));
        }
        return result.toString();
    }
}

