import java.util.ArrayList;
import java.util.Comparator;
import java.util.List;

public class Main {

    public static void main(String[] args) {
        List<Integer> list = new ArrayList<>();
        list.add(3);
        list.add(1);
        list.add(5);
        list.add(2);
        list.add(4);
        list.add(6);

        Comparator<Integer> comparator = Comparator.naturalOrder();

        List<Integer> result = between(list, 2, 5, comparator, true, true);
        System.out.println(result);
        // Output: [2, 3, 4, 5]
    }

    public static <T> List<T> between(List<T> list, T fromElement, T toElement,
                                       Comparator<? super T> comparator,
                                       boolean fromInclusive, boolean toInclusive) {
        List<T> result = new ArrayList<>();
        boolean startAdding = false;
        for (T item : list) {
            int comparisonFrom = comparator.compare(item, fromElement);
            int comparisonTo = comparator.compare(item, toElement);
            if ((comparisonFrom > 0 || (fromInclusive && comparisonFrom == 0)) &&
                    (comparisonTo < 0 || (toInclusive && comparisonTo == 0))) {
                startAdding = true;
                result.add(item);
            } else if (comparisonTo > 0 && startAdding) {
                break;
            }
        }
        return result;
    }
}

