public ClassLoader getAllInterfacesClassLoader(ClassLoader loader, Class<?>[] interfaces) {
    // Check if the loader can see all interfaces
    for (Class<?> iface : interfaces) {
        try {
            Class<?> c = loader.loadClass(iface.getName());
            if (!iface.isAssignableFrom(c)) {
                throw new IllegalArgumentException("Interface not assignable from class: " + iface.getName());
            }
        } catch (ClassNotFoundException e) {
            // Interface not found in this loader, so continue
            continue;
        }
    }

    // Return the given loader since it can see all interfaces
    return loader;

    // If the given loader cannot see all interfaces, then return a combined classloader of all interface classloaders
    List<ClassLoader> classLoaders = new ArrayList<>();
    for (Class<?> iface : interfaces) {
        ClassLoader cl = iface.getClassLoader();
        if (cl != null && !classLoaders.contains(cl)) {
            classLoaders.add(cl);
        }
    }
    if (classLoaders.isEmpty()) {
        throw new IllegalArgumentException("No classloaders found for the given interfaces");
    } else if (classLoaders.size() == 1) {
        return classLoaders.get(0);
    } else {
        return new URLClassLoader(new URL[0], new CombinedClassLoader(classLoaders));
