public static double[] forwardSubstitution(double[][] U, double[] b) {
    int n = U.length;
    double[] x = new double[n];

    for (int i = 0; i < n; i++) {
        x[i] = b[i];
        for (int j = 0; j < i; j++) {
            x[i] -= U[i][j] * x[j];
        }
        x[i] /= U[i][i];
    }

    return x;
}
