public static double computeEigenValue(double[][] A, double[] eigenVector) {
    double numerator = 0;
    double denominator = 0;
    int n = A.length;

    // Compute numerator and denominator
    for (int i = 0; i < n; i++) {
        double temp = 0;
        for (int j = 0; j < n; j++) {
            temp += A[i][j] * eigenVector[j];
        }
        numerator += eigenVector[i] * temp;
        denominator += eigenVector[i] * eigenVector[i];
    }

    // Compute eigen value
    double eigenValue = numerator / denominator;
    return eigenValue;
