public static double[] calculateGammas(double[] x) {
    int n = x.length;
    double[] gammas = new double[n];

    for (int i = 0; i < n; i++) {
        double sigma = 0.0;
        for (int j = i; j < n; j++) {
            sigma += x[j] * x[j];
        }

        double mu;
        if (x[i] > 0) {
            mu = Math.sqrt(sigma);
        } else {
            mu = -Math.sqrt(sigma);
        }

        double vi = x[i] + mu;
        gammas[i] = vi / mu;

        for (int j = i + 1; j < n; j++) {
            x[j] /= vi;
        }
    }

    return gammas;
}

