public static double populationCovariance(final double[] xArray, final double[] yArray) throws DimensionMismatchException, MathIllegalArgumentException {
    double covariance = Double.NaN;
    final int length = xArray.length;
    if (length != yArray.length) {
        throw new DimensionMismatchException(yArray.length, length);
    }
    if (length > 1) {
        double xMean = 0.0;
        double yMean = 0.0;
        for (int i = 0; i < length; i++) {
            xMean += xArray[i];
            yMean += yArray[i];
        }
        xMean /= length;
        yMean /= length;
        double sum = 0.0;
        for (int i = 0; i < length; i++) {
            final double xDev = xArray[i] - xMean;
            final double yDev = yArray[i] - yMean;
            sum += xDev * yDev;
        }
        covariance = sum / (length);
    } else {
        if (length == 1) {
            covariance = 0.0;
        } else {
            throw new MathIllegalArgumentException(LocalizedFormats.INSUFFICIENT_DIMENSION,
                                                   new Object[] { length, 1 });
        }
    }
    return covariance;
}
