public class Householder {
    public static void multiply(Matrix Q, Matrix B) {
        int m = Q.getRowDimension();
        int n = Q.getColumnDimension();
        int k = B.getColumnDimension();
        double[] u = new double[m];

        for (int j = 0; j < k; j++) {
            for (int i = m - 1; i >= 0; i--) {
                u[i] = Q.get(i, n - 1);
                for (int l = n - 2; l >= i; l--) {
                    u[i] -= Q.get(i, l) * u[l];
                }
            }
            for (int i = 0; i < m; i++) {
                u[i] *= 2.0 / (Q.get(i, i) * Q.get(i, i));
            }
            for (int i = 0; i < m; i++) {
                for (int l = 0; l < k; l++) {
                    B.set(i, l, B.get(i, l) - u[i] * Q.get(i, n - 1) * B.get(m - 1, l));
                }
            }
            for (int i = 0; i < m; i++) {
                for (int l = n - 2; l >= i; l--) {
                    B.set(i, j, B.get(i, j) - u[i] * Q.get(i, l) * B.get(m - 1, j));
                    B.set(m - 1, j, B.get(m - 1, j) - u[i] * Q.get(i, l) * B.get(i, j));
                }
            }
        }
    }
}

