import com.github.javaparser.StaticJavaParser;
import com.github.javaparser.ast.CompilationUnit;
import com.github.javaparser.ast.expr.StringLiteralExpr;
import com.github.javaparser.ast.visitor.ModifierVisitor;
import com.github.javaparser.ast.visitor.Visitable;

public class StringParser {

    public static void main(String[] args) {
        String input = "public class MyClass { public static void main(String[] args) { System.out.println(\"Hello, world!\"); } }";
        String oldString = "Hello";
        String newString = "Hi";

        // Parse the input string into a CompilationUnit AST node
        CompilationUnit cu = StaticJavaParser.parse(input);

        // Create a ModifierVisitor to replace StringLiteralExpr nodes with the specified substitutions
        ModifierVisitor<Void> modifierVisitor = new ModifierVisitor<>() {
            @Override
            public Visitable visit(StringLiteralExpr n, Void arg) {
                String literalValue = n.getValue();
                if (literalValue.contains(oldString)) {
                    String newLiteralValue = literalValue.replace(oldString, newString);
                    n.setString(newLiteralValue);
                }
                return super.visit(n, arg);
            }
        };

        // Apply the modifierVisitor to the CompilationUnit AST node to perform substitutions
        cu.accept(modifierVisitor, null);

        // Print out the modified CompilationUnit as a string
        System.out.println(cu.toString());
    }
}

