public class BucketIndexer {

    public static int getBucketIndex(ByteBuffer key, int numBuckets) {
        // Compute the CRC32 checksum of the key's bytes
        CRC32 crc32 = new CRC32();
        crc32.update(key);
        long checksum = crc32.getValue();

        // Map the checksum to a bucket index in the range [0, numBuckets - 1]
        int bucketIndex = (int) (checksum % numBuckets);
        if (bucketIndex < 0) {
            bucketIndex += numBuckets;
        }
