import org.webrtc.IceServer;
import org.webrtc.PeerConnection;
import org.webrtc.PeerConnectionFactory;

public class TurnServerConnection {

    private PeerConnection peerConnection;

    public void connectToTurnServer(String turnServerUrl, String turnUsername, String turnPassword) {

        // Create the ICE server configuration with the TURN server details
        IceServer iceServer = IceServer.builder(turnServerUrl)
                .setUsername(turnUsername)
                .setPassword(turnPassword)
                .createIceServer();

        // Create the peer connection factory and add the ICE server configuration
        PeerConnectionFactory peerConnectionFactory = PeerConnectionFactory.builder().createPeerConnectionFactory();
        peerConnectionFactory.setOptions(new PeerConnectionFactory.Options());
        peerConnectionFactory.getOptions().disableEncryption = true;
        peerConnectionFactory.getOptions().disableNetworkMonitor = true;
        PeerConnection.RTCConfiguration rtcConfig = new PeerConnection.RTCConfiguration(Arrays.asList(iceServer));
        rtcConfig.tcpCandidatePolicy = PeerConnection.TcpCandidatePolicy.DISABLED;

        // Create the peer connection object and set the configuration
        peerConnection = peerConnectionFactory.createPeerConnection(rtcConfig);
    }
}

