import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Arrays;
import java.util.List;

public class HttpConnectionExample {

    public static void main(String[] args) throws IOException {
        String urlString = "https://example.com/api";
        URL url = new URL(urlString);

        HttpURLConnection connection = (HttpURLConnection) url.openConnection();

        // Set the nonProxyHosts property value
        String nonProxyHostsString = System.getProperty("http.nonProxyHosts");
        if (nonProxyHostsString != null && !nonProxyHostsString.isEmpty()) {
            List<String> nonProxyHosts = Arrays.asList(nonProxyHostsString.split("\\|"));
            String host = url.getHost();
            if (nonProxyHosts.contains(host)) {
                // Skip proxy for this host
                connection.setUseProxy(false);
            }
        }

        // Send the HTTP request and read the response
        connection.setRequestMethod("GET");
        connection.setRequestProperty("Content-Type", "application/json");
        BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
        String line;
        StringBuilder response = new StringBuilder();
        while ((line = reader.readLine()) != null) {
            response.append(line);
        }
        reader.close();

        // Print the HTTP response
        System.out.println(response.toString());
    }
}

