public static boolean isChecksumCorrect(ByteBuffer buffer) {
    // Get the checksum from the buffer
    short checksum = buffer.getShort(buffer.limit() - 2);

    // Reset the buffer's position and limit to calculate the checksum
    buffer.position(0);
    buffer.limit(buffer.limit() - 2);

    // Calculate the checksum
    int sum = 0;
    while (buffer.hasRemaining()) {
        sum += buffer.get() & 0xFF;
    }
    sum = (sum & 0xFFFF) + (sum >>> 16);
    sum = ~sum;

    // Check if the calculated checksum matches the one in the buffer
    return (checksum == sum);
}

