public class PassTimer {
    private long startTime; // time when the timer was started
    private long elapsedTime; // total elapsed time since the timer was started
    private boolean isRunning; // flag indicating whether the timer is currently running

    public PassTimer() {
        isRunning = false;
    }

    // starts the timer
    public void start() {
        if (!isRunning) {
            startTime = System.currentTimeMillis();
            isRunning = true;
        }
    }

    // stops the timer
    public void stop() {
        if (isRunning) {
            elapsedTime += System.currentTimeMillis() - startTime;
            isRunning = false;
        }
    }

    // returns the total elapsed time in milliseconds
    public long getElapsedTime() {
        if (isRunning) {
            return System.currentTimeMillis() - startTime + elapsedTime;
        } else {
            return elapsedTime;
        }
    }
}

