import java.nio.file.Files;
import java.nio.file.attribute.PosixFilePermission;
import java.util.Set;

public class UmaskExample {
    // The default umask value, e.g. 022
    private static final int DEFAULT_UMASK = 022;

    public static void main(String[] args) throws Exception {
        // Set the default umask for the current process
        int oldUmask = setUmask(DEFAULT_UMASK);

        // Create a new file with default permissions
        Files.createFile("/path/to/new/file");

        // Restore the old umask value
        setUmask(oldUmask);
    }

    // Sets the umask for the current process and returns the previous value
    private static int setUmask(int umask) throws Exception {
        // Get the current umask value
        int oldUmask = getUmask();

        // Set the new umask value
        int newUmask = ~umask & 0777;
        Runtime.getRuntime().exec("umask " + newUmask).waitFor();

        // Return the old umask value
        return oldUmask;
    }

    // Returns the current umask value as an integer
    private static int getUmask() throws Exception {
        Process process = Runtime.getRuntime().exec("umask");
        process.waitFor();
        String output = new String(process.getInputStream().readAllBytes());
        String[] parts = output.trim().split("");
        int umask = 0;
        for (String part : parts) {
            int digit = Character.digit(part.charAt(0), 8);
            if (digit >= 0 && digit <= 7) {
                umask = (umask << 3) + digit;
            }
        }
        return umask;
    }
}

