import java.nio.charset.StandardCharsets;
import java.util.regex.Pattern;

public class PercentEncodingExample {
    public static String encode(String value) {
        String encoded = java.net.URLEncoder.encode(value, StandardCharsets.UTF_8);

        // Make additional replacements for RFC 3986 compatibility
        encoded = encoded.replaceAll("\\+", "%20");
        encoded = encoded.replaceAll("\\%21", "!");
        encoded = encoded.replaceAll("\\%27", "'");
        encoded = encoded.replaceAll("\\%28", "(");
        encoded = encoded.replaceAll("\\%29", ")");
        encoded = encoded.replaceAll("\\%7E", "~");

        // Unescape any percent-encoded characters that are not reserved
        Pattern pattern = Pattern.compile("(%[0-9a-fA-F]{2})");
        encoded = pattern.matcher(encoded).replaceAll(match -> {
            String hex = match.group(1).substring(1);
            int code = Integer.parseInt(hex, 16);
            if (isUnreserved(code)) {
                return Character.toString((char)code);
            } else {
                return match.group(1);
            }
        });

        return encoded;
    }

    private static boolean isUnreserved(int code) {
        return (code >= 'A' && code <= 'Z')
            || (code >= 'a' && code <= 'z')
            || (code >= '0' && code <= '9')
            || code == '-' || code == '.' || code == '_' || code == '~';
    }
}

