public DAVResource getResource(URI uri) throws DAVException {
    // If URI is relative, resolve it against the root of this DAVRepository
    if (!uri.isAbsolute()) {
        uri = rootUri.resolve(uri);
    }

    // Create HTTP GET request for specified URI
    HttpGet request = new HttpGet(uri);

    try (CloseableHttpResponse response = client.execute(request)) {
        // If response code is not OK, throw exception
        if (response.getStatusLine().getStatusCode() != HttpStatus.SC_OK) {
            throw new DAVException("Unable to get resource: " + uri + ". Response: "
                    + response.getStatusLine().toString());
        }

        // Create DAVResource from HTTP response
