public class FileRenamer {

    public static void renameAndSave(List<String> fileLocations, String newFileName) {
        for (String fileLocation : fileLocations) {
            File oldFile = new File(fileLocation);
            if (!oldFile.exists()) {
                System.out.println("File does not exist: " + fileLocation);
                continue;
            }
            String directoryPath = oldFile.getParent();
            String extension = getFileExtension(oldFile);
            String newFilePath = directoryPath + "/" + newFileName + extension;
            File newFile = new File(newFilePath);
            if (newFile.exists()) {
                System.out.println("File already exists: " + newFilePath);
                continue;
            }
            boolean success = oldFile.renameTo(newFile);
            if (!success) {
                System.out.println("Failed to rename file: " + fileLocation);
            }
        }
    }

    private static String getFileExtension(File file) {
        String name = file.getName();
        int lastDotIndex = name.lastIndexOf(".");
        if (lastDotIndex > 0) {
            return name.substring(lastDotIndex);
        }
        return "";
    }

    public static void main(String[] args) {
        List<String> fileLocations = List.of("C:/myfolder/file1.txt", "C:/myfolder/file2.txt");
        String newFileName = "newfile";
        renameAndSave(fileLocations, newFileName);
    }
}

