public static void intToLittleEndianBytes(int x, byte[] buf, int offset) {
    buf[offset] = (byte) (x & 0xff);
    buf[offset + 1] = (byte) ((x >> 8) & 0xff);
    buf[offset + 2] = (byte) ((x >> 16) & 0xff);
    buf[offset + 3] = (byte) ((x >> 24) & 0xff);
}
