import javax.jms.*;
import javax.naming.*;

public class MessageSequenceWaiter {

  public void waitForAcknowledgement(int sequenceNumber) throws NamingException, JMSException, InterruptedException {

    // Setting up JNDI context to look up the JMS connection factory and destination
    Context context = new InitialContext();
    ConnectionFactory connectionFactory = (ConnectionFactory) context.lookup("java:/jms/MyConnectionFactory");
    Destination destination = (Destination) context.lookup("java:/jms/MyDestination");

    // Creating JMS connection and session
    Connection connection = connectionFactory.createConnection();
    Session session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);

    // Creating JMS consumer with a message selector to only receive messages with the given sequence number
    MessageConsumer consumer = session.createConsumer(destination, "JMSMessageID='" + sequenceNumber + "'");

    // Starting JMS connection
    connection.start();

    // Waiting for acknowledgement
    while (true) {
      Message message = consumer.receive();
      if (message != null) {
        System.out.println("Sequence number " + sequenceNumber + " has been acknowledged.");
        break;
      }
      Thread.sleep(1000); // Wait for 1 second before checking again
    }

    // Closing resources
    consumer.close();
    session.close();
    connection.close();
  }
}

