            {"Magenta", "#FF00FF"},
            {"Gray", "#808080"},
            {"DarkGray", "#A9A9A9"},
            {"LightGray", "#D3D3D3"}
    };

    public static String getColorNameFromHex(String hexColor) {
        // Convert hex string to integer
        int colorInt = Color.parseColor(hexColor);
        // Check if the integer color value matches a named color
        for (String[] color : COLORS) {
            if (colorInt == Color.parseColor(color[1])) {
                return color[0];
            }
        }
        // If no named color matches, return null
        return null;
    }
}

