import java.io.File;
import java.nio.file.Files;
import java.nio.file.attribute.PosixFilePermission;
import java.util.HashSet;
import java.util.Set;

public class FilePermissionExample {

    public static void main(String[] args) {
        // Path of the file to be modified
        String filePath = "/path/to/file.txt";

        // Create a File object from the path
        File file = new File(filePath);

        // Check if the file exists and is a regular file
        if (file.exists() && file.isFile()) {
            try {
                // Create a new set of permissions
                Set<PosixFilePermission> permissions = new HashSet<>();

                // Add the desired permissions
                permissions.add(PosixFilePermission.OWNER_READ);
                permissions.add(PosixFilePermission.OWNER_WRITE);
                permissions.add(PosixFilePermission.GROUP_READ);
                permissions.add(PosixFilePermission.OTHERS_READ);

                // Set the new permissions
                Files.setPosixFilePermissions(file.toPath(), permissions);

                System.out.println("File permissions changed successfully.");

            } catch (Exception e) {
                System.err.println("Error changing file permissions: " + e.getMessage());
            }
        } else {
            System.err.println("File does not exist or is not a regular file.");
        }
    }

}

