public void generateFactoryMapping(Class<?> clazz) {
    try {
        // Get the class name
        String className = clazz.getSimpleName();

        // Get the constructor for the class
        Constructor<?> constructor = clazz.getConstructor();

        // Generate the factory mapping skeleton
        String factoryMapping = "Factory.create(\"" + className + "\", new Object[] {";

        // Get the parameter types for the constructor
        Class<?>[] parameterTypes = constructor.getParameterTypes();

        // Generate the parameter list for the constructor
        String parameterList = "";
        for (Class<?> parameterType : parameterTypes) {
            parameterList += parameterType.getSimpleName() + ".class, ";
        }
        if (parameterList.endsWith(", ")) {
            parameterList = parameterList.substring(0, parameterList.length() - 2);
        }

        // Add the parameter list to the factory mapping skeleton
        factoryMapping += parameterList + "});";

        // Print the factory mapping skeleton
        System.out.println(factoryMapping);
    } catch (NoSuchMethodException e) {
        System.err.println("Error generating factory mapping: " + e.getMessage());
    }
}

