            // Other range is fully inside this range, remove it
            end = other.start - 1;
        } else if (other.start <= start && other.end >= end) {
            // This range is fully inside other range, remove it
            start = end + 1;
            end = start - 1;
        } else if (other.start <= start && other.end >= start && other.end <= end) {
            // Other range overlaps with the start of this range, truncate this range
            start = other.end + 1;
        } else if (other.start >= start && other.start <= end && other.end >= end) {
            // Other range overlaps with the end of this range, truncate this range
