import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.stream.StreamResult;
import javax.xml.transform.stream.StreamSource;
import java.io.File;
import java.io.FileOutputStream;
import java.io.OutputStream;

public class XMLTransformer {
    public void setStylesheet(String xmlFilePath, String xslFilePath, String outputFilePath) throws Exception {
        // Create a TransformerFactory
        TransformerFactory transformerFactory = TransformerFactory.newInstance();

        // Create a Transformer
        Transformer transformer = transformerFactory.newTransformer(new StreamSource(new File(xslFilePath)));

        // Set the input XML file
        StreamSource xmlSource = new StreamSource(new File(xmlFilePath));

        // Set the output file
        OutputStream outputStream = new FileOutputStream(outputFilePath);
        StreamResult outputResult = new StreamResult(outputStream);

        // Transform the XML using the XSL stylesheet
        transformer.transform(xmlSource, outputResult);

        // Close the output stream
        outputStream.close();
    }

