        // Example usage: set the stylesheet for an XML file
        XMLTransformer transformer = new XMLTransformer();
        try {
            transformer.setStylesheet("input.xml", "stylesheet.xsl", "output.xml");
            System.out.println("Stylesheet set successfully.");
        } catch (Exception e) {
            System.err.println("Error setting stylesheet: " + e.getMessage());
            e.printStackTrace();
        }
    }
