import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;

public class ResponseReader {
    public String readResponseString(String urlString) {
        StringBuilder response = new StringBuilder();
        HttpURLConnection connection = null;
        BufferedReader reader = null;

        try {
            // Create URL object from the provided URL string
            URL url = new URL(urlString);

            // Open connection to the server
            connection = (HttpURLConnection) url.openConnection();

            // Set request method to GET
            connection.setRequestMethod("GET");

            // Get input stream from the connection
            reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));

            // Read response line by line
            String line;
            while ((line = reader.readLine()) != null) {
                response.append(line);
            }
        } catch (IOException e) {
            System.err.println("Error reading response from server: " + e.getMessage());
            e.printStackTrace();
        } finally {
            // Close BufferedReader and HttpURLConnection
            if (reader != null) {
                try {
                    reader.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            if (connection != null) {
                connection.disconnect();
            }
        }

        return response.toString();
    }

    public static void main(String[] args) {
        // Example usage: read response from server
        ResponseReader responseReader = new ResponseReader();
        String urlString = "https://www.example.com/api/data"; // Replace with actual URL
        String response = responseReader.readResponseString(urlString);
        System.out.println("Response from server: " + response);
    }
}

