import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.SocketChannel;
import java.nio.charset.StandardCharsets;

public class InterestOpsExample {
    private static final int BUFFER_SIZE = 1024;
    private ByteBuffer readBuffer = ByteBuffer.allocate(BUFFER_SIZE);
    private ByteBuffer writeBuffer = ByteBuffer.allocate(BUFFER_SIZE);
    private SelectionKey selectionKey;
    private SocketChannel socketChannel;

    public InterestOpsExample(SocketChannel socketChannel) throws IOException {
        this.socketChannel = socketChannel;
        this.socketChannel.configureBlocking(false);
        // Register the socket channel with a selector
        this.selectionKey = socketChannel.register(selector, SelectionKey.OP_READ, this);
    }

    // Method to activate interest in writing from a thread other than the selector thread
    public void activateInterestInWrite() {
        // Set interestOps to OP_WRITE
        selectionKey.interestOps(selectionKey.interestOps() | SelectionKey.OP_WRITE);
        // Wake up the selector to process the OP_WRITE interest
        selector.wakeup();
    }

    // Method to handle read operation
    private void handleRead() throws IOException {
        readBuffer.clear();
        int bytesRead = socketChannel.read(readBuffer);
        if (bytesRead == -1) {
            // Connection closed by remote peer
            selectionKey.cancel();
            socketChannel.close();
        } else if (bytesRead > 0) {
            // Process the read data
            readBuffer.flip();
            String receivedData = StandardCharsets.UTF_8.decode(readBuffer).toString();
            System.out.println("Received data: " + receivedData);
            // Example: Activate interest in writing from a different thread
            activateInterestInWrite();
        }
    }

    // Method to handle write operation
    private void handleWrite() throws IOException {
        writeBuffer.clear();
        // Prepare data to be written
        String sendData = "Hello, World!";
        writeBuffer.put(sendData.getBytes());
        writeBuffer.flip();
        // Write data to the socket channel
        while (writeBuffer.hasRemaining()) {
            socketChannel.write(writeBuffer);
        }
        // Clear OP_WRITE interest
        selectionKey.interestOps(selectionKey.interestOps() & ~SelectionKey.OP_WRITE);
    }

    // Selector loop
    private void selectorLoop() throws IOException {
        while (true) {
            selector.select();
            for (SelectionKey key : selector.selectedKeys()) {
                if (key.isReadable()) {
                    handleRead();
                } else if (key.isWritable()) {
                    handleWrite();
                }
            }
            selector.selectedKeys().clear();
        }
    }

    public static void main(String[] args) throws IOException {
        SocketChannel socketChannel = SocketChannel.open();
        socketChannel.configureBlocking(false);
        socketChannel.connect(/* specify server address */);
        InterestOpsExample interestOpsExample = new InterestOpsExample(socketChannel);
        // Start selector loop in a separate thread
        new Thread(() -> {
            try {
                interestOpsExample.selectorLoop();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }).start();
    }
}

